export default class Billing {
    address: string;
    city: string;
    country: string;
    state: string;
    zip: string;
    phone: string;
    /**
     * Valida que los campos del Billing estén completos y correctos
     *
     * @return @boolean
     */
    _validate(): boolean;
    /**
     * Establece el nombre de la ciudad del comprador
     *
     * @param {string} value - Nombre de la ciudad
     */
    setCity(value: string): Billing;
    /**
     * Establece el nombre del estado del comprador
     *
     * @param {string} value - Nombre del estado
     */
    setState(value: string): Billing;
    /**
     * Establece el nombre del país del comprador
     *
     * @param {string} value - Nombre del país del comprador
     */
    setCountry(value: string): Billing;
    /**
     * Establece el código postal del comprador
     *
     * @param {string} value - Código postal
     */
    setZip(value: string): Billing;
    /**
     * Establce la dirección del comprador
     *
     * @param {string} value - Dirección
     */
    setAddress(value: string): Billing;
    /**
     * Establece el número telefónico del cliente.
     *
     * @param {String} value
     */
    setPhoneNumber(value: string): Billing;
}
