export default class Card {
    token: string;
    number: string;
    cvv2: string;
    expire_month: string;
    expire_year: string;
    cardholder: string;
    customer: string;
    reference: string;
    /**
     * @deprecated since version 1.0.30
     */
    pan: string;
    cvc: string;
    card_holder: string;
    exp_month: string;
    exp_year: string;
    customer_token: string;
    /**
     * Valida los campos requeridos  para agregar una tarjeta
     *
     * @returns {boolean}
     */
    _validate(): boolean;
    /**
     * Establece el número de la tarjeta
     *
     * @param {Number} Numeros de la tarjeta
     */
    setCardNumber(number: any): Error | Card;
    /**
     * Establece el número de seguridad de la tarjeta
     *
     * @param {Number} cvv
     */
    setCvv(cvv: number): Error | Card;
    /**
     * Establece el nombre y apellido del tarjetahabiente
     *
     * @param {String} holder
     */
    setCardHolder(holder: string): Card;
    /**
     * Establece el mes y año de expiración de la tarjeta
     *
     * @param {Number} date
     */
    setExpirationDate(date: number): Card;
    /**
     * Establece el token de una tarjeta
     *
     * @param {String} token
     * @param {String} reference
     */
    setToken(token: string, reference?: string): Card;
    /**
     * Establece el token de un cliente
     *
     * @param {String} token
     */
    setCustomerToken(token: string): Card;
}
