export default class Item {
    code: string;
    title: string;
    price: number;
    qty: number;
    tax: number;
    total: number;
    /**
     * Calcula precio total de los items agregados
     *
     */
    _totalize(): void;
    /**
     * Valida que un String no este vacío
     *
     * @returns {boolean}
     */
    _validate(): boolean;
    /**
     * Establece el código del producto
     *
     * @param {string} code
     * @returns {Item}
     */
    setCode(code: string): Item;
    /**
     * Establece la descripcion del producto
     *
     * @param {string} description - Descripcion
     * @returns {Item} Item
     */
    setDescription(description: string): Item;
    /**
     * Establece el precio total del producto(s) agregados
     *
     * @param {float} price - Precio total del producto
     * @returns {Item} Item
     */
    setPrice(price: float): Item;
    /**
     * Establece la cantidad de productos a agregar
     *
     * @param {number} qty - cantidad
     * @default [qty = 1]
     * @returns {Item} Item
     */
    setQuantity(qty: number): Item;
    /**
     * Establce el impuesto total del producto(s) agregados
     *
     * @param {float} tax - Impuesto
     * @returns {Item} Item
     */
    setTaxAmount(tax: float): Item;
}
