export default class Modal {
    is_open: boolean;
    modal: Element;
    /**
     * Método para crear el modal de pago
     */
    _createModal(): HTMLDivElement;
    /**
     * Crea el conenedor del modal
     */
    _createContainer(): HTMLDivElement;
    /**
     * Crea elemento de posisción
     */
    _createOverlay(): HTMLDivElement;
    /**
     * Crea el botón para cerrar la ventana de cobro
     */
    _createButtonClose(): HTMLButtonElement;
    /**
     * Crea el elemento Iframe en el DOM
     *
     * @param {*} url - URL que se visualizara en el iframe
     * @param {*} callback
     */
    _createContent(url: any, callback: any): HTMLIFrameElement;
    /**
     * Evalua las respuestas enviadas por @method postMessage()
     * desde el servidor de la URL agregada al iFrame
     *
     * @param {object} event
     * @param {*} resolve
     * @param {*} reject
     */
    _evaluatePostMessage(event: object, resolve: any, reject: any, closeOnFail: any): void;
    /**
     * Método encargado de ejecutar al proceso de carga del Iframe
     *
     * @param {*} url
     * @returns {Promise}
     */
    load(url: any, closeOnFail?: boolean, fullscreen?: boolean): Promise<any>;
    /**
     * Método encargado de ejecutar al proceso de carga del Iframe
     *
     * @param {*} payload
     * @returns
     */
    loadPayload(payload: any): Promise<any>;
    /**
     * Metodo para visualizar el modal en el DOM
     */
    open(): void;
    /**
     * Método para cerrar el modal
     */
    close(): void;
}
